  description: weekly.value?.summary,
  ogTitle: weekly.value?.title,
  ogDescription: weekly.value?.summary,
  twitterTitle: weekly.value?.title,
  twitterDescription: weekly.value?.summary,
})

const { progress } = useReadingProgress()
